import { useState, useRef } from 'react';

const AudioRecorder = ({ onNewAudio }) => {
  const [isRecording, setIsRecording] = useState(false);
  const mediaRecorderRef = useRef(null);
  const audioChunksRef = useRef([]);

  const sendAudioToServer = async (audioBlob) => {
    try {
      const formData = new FormData();
      formData.append('audio', audioBlob, 'recording.wav');
      
      const response = await fetch('http://localhost:8000/api/upload-audio', {
        method: 'POST',
        body: formData
      });
      
      const result = await response.json();
      console.log('Аудио отправлено:', result);
    } catch (error) {
      console.error('Ошибка отправки:', error);
    }
  };

  const startRecording = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      mediaRecorderRef.current = new MediaRecorder(stream);
      
      mediaRecorderRef.current.ondataavailable = (event) => {
        audioChunksRef.current.push(event.data);
      };
      
      mediaRecorderRef.current.onstop = async () => {
        const audioBlob = new Blob(audioChunksRef.current, { type: 'audio/wav' });
        
        // Преобразуем аудио в нужный формат (моно, 16 кГц) перед отправкой
        const audioUrl = URL.createObjectURL(audioBlob);
        onNewAudio(audioUrl);

        const audioFile = await convertAudioToWav(audioBlob);
        sendAudioToServer(audioFile);  // Отправляем на сервер
        audioChunksRef.current = [];
      };
      
      mediaRecorderRef.current.start();
      setIsRecording(true);
    } catch (err) {
      console.error('Ошибка доступа к микрофону:', err);
    }
  };

  const stopRecording = () => {
    if (mediaRecorderRef.current) {
      mediaRecorderRef.current.stop();
      mediaRecorderRef.current.stream.getTracks().forEach(track => track.stop());
      setIsRecording(false);
    }
  };

  // Преобразование аудио в правильный формат (моно, 16 кГц)
  const convertAudioToWav = async (audioBlob) => {
    const audioContext = new (window.AudioContext || window.webkitAudioContext)();
    const arrayBuffer = await audioBlob.arrayBuffer();
    const audioBuffer = await audioContext.decodeAudioData(arrayBuffer);

    // Преобразуем в моно (если несколько каналов) и изменяем частоту дискретизации
    const numberOfChannels = 1;  // Моно
    const sampleRate = 16000;     // Частота 16 кГц
    const offlineContext = new OfflineAudioContext(numberOfChannels, audioBuffer.length, sampleRate);
    const source = offlineContext.createBufferSource();
    source.buffer = audioBuffer;
    source.connect(offlineContext.destination);

    source.start();
    const renderedBuffer = await offlineContext.startRendering();

    // Преобразуем в формат WAV
    const wavBlob = await bufferToWavBlob(renderedBuffer);
    return wavBlob;
  };

  // Функция для преобразования ArrayBuffer в WAV
  const bufferToWavBlob = async (buffer) => {
    const wavArrayBuffer = audioBufferToWav(buffer);
    return new Blob([wavArrayBuffer], { type: 'audio/wav' });
  };

  const audioBufferToWav = (buffer) => {
    // Преобразование AudioBuffer в WAV (нужна специальная библиотека или код, чтобы это сделать)
    // Для простоты, можно использовать библиотеку, как 'wav-encoder', чтобы это реализовать
    // Возвращаем ArrayBuffer с WAV данными
    // Например, с помощью библиотеки https://github.com/mik3y/wav-encoder
    return new Uint8Array();
  };

  return (
    <div className="audio-recorder">
      {isRecording ? (
        <button className="stop-button" onClick={stopRecording}>
          Остановить запись
        </button>
      ) : (
        <button className="start-button" onClick={startRecording}>
          Записать голосовое сообщение
        </button>
      )}
    </div>
  );
};

export default AudioRecorder;
